local combat = createCombatObject()setCombatParam(combat, COMBAT_PARAM_EFFECT, CONST_ME_MAGIC_BLUE)setCombatParam(combat, COMBAT_PARAM_AGGRESSIVE, 0)local condition = createConditionObject(CONDITION_ATTRIBUTES)setConditionParam(condition, CONDITION_PARAM_TICKS, 100000)setConditionParam(condition, CONDITION_PARAM_SKILL_MELEE, 90)setConditionParam(condition, CONDITION_PARAM_SKILL_SHIELD, 90)setConditionParam(condition, CONDITION_PARAM_SKILL_DISTANCE, 90)setConditionParam(condition, CONDITION_PARAM_STAT_MAGICLEVEL, 2)setCombatCondition(combat, condition)local condition = createConditionObject(CONDITION_HASTE)setConditionParam(condition, CONDITION_PARAM_SPEED, 200)setConditionParam(condition, CONDITION_PARAM_TICKS, 100000)setConditionParam(condition, CONDITION_PARAM_BUFF, TRUE)setCombatCondition(combat, condition)local condition = createConditionObject(CONDITION_REGENERATION)setConditionParam(condition, CONDITION_PARAM_SUBID, 1)setConditionParam(condition, CONDITION_PARAM_BUFF, TRUE)setConditionParam(condition, CONDITION_PARAM_TICKS, 2 * 60 * 1000)setConditionParam(condition, CONDITION_PARAM_HEALTHGAIN, 75)setConditionParam(condition, CONDITION_PARAM_HEALTHTICKS, 1000)setCombatCondition(combat, condition)function onCastSpell(cid, var)return doCombat(cid, combat, var)end